import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import CommentsForm from "./CommentsForm";
import { __getgames } from "../../reduex/modules/gameInfoSlice";

const GameInfo = () => {
  const { id } = useParams();
  console.log("파람 아이디", id);
  const dispatch = useDispatch();

  const game = useSelector((state) => state.games.games);

  useEffect(() => {
    dispatch(__getgames());
  }, [dispatch]);

  return (
    <GameInfoContainer>
      {/* 게임 디테일 이미지 */}
      <Wrap>
        <ImgContainer>
          <TitleBox key={game.id}>{game.title}</TitleBox>
          <ImgBox>이미지 자리</ImgBox>
        </ImgContainer>
        {/* 게임 디테일 컨텐츠 */}
        <ContentsContainer>
          <TitleBox>contents</TitleBox>
          <ContentsBox>contents text</ContentsBox>
        </ContentsContainer>
      </Wrap>
      {/* 코멘트 달리는 폼 */}
      <CommentsForm />
    </GameInfoContainer>
  );
};

export default GameInfo;

const GameInfoContainer = styled.div`
  display: grid;
  align-items: center;
  justify-content: center;
`;
const Wrap = styled.div`
  display: flex;
  gap: 10px;
`;
const ImgContainer = styled.div`
  border: 1px solid white;

  width: 500px;
  height: 250px;

  margin: 10px auto;
`;
const TitleBox = styled.div`
  width: 200px;
  height: 30px;

  font-size: 30px;

  margin-top: 5px;
  margin-left: 5px;

  border: 1px solid white;
`;

const ImgBox = styled.div`
  width: 500px;
  height: 200px;

  margin: 2px auto;

  border: 1px solid white;

  background-color: wheat;
`;
const ContentsContainer = styled.div`
  border: 1px solid white;

  width: 350px;
  height: 250px;

  margin: 10px auto;
`;
const ContentsBox = styled.div`
  border: 1px solid white;

  width: 350px;
  height: 200px;

  margin: 5px auto;
  background-color: wheat;
`;
